package string;

public class String {

	public static void main(java.lang.String[] args) {
		// TODO Auto-generated method stub
		java.lang.String myString= "Hello  World ";
		int stringlegth=myString.length();
		java.lang.String myStringtolowercase=myString.toLowerCase();
		java.lang.String myStringtouppercase=myString.toUpperCase();
		System.out.println(myString);
		System.out.println(stringlegth);
		System.out.println(myStringtolowercase);
		System.out.println(myStringtouppercase);
		System.out.println(myString.indexOf('0'));

	}

}
