
public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		short x=-32000;//1 byte
		byte y=-127;//2 bytes
		int z=2000000000;//4 bytes
		long p=1000000000;//8 bytes
		float q=(float)5.4;//4 bytes
		double s=365.345;//8 bytes
		char t='E';//2 bytes & boolean 1 byte
		boolean is_true=false;
		System.out.println(y+" "+x+" "+z+" "+p+" "+q+" "+s+" "+t+" "+is_true);

	}

}
